mapper.select:

select  hostNo
from    host
where	hostNo = #{hostNo}


select NVL(SUM(clean), 0) + NVL(SUM(food), 0) + NVL(SUM(play), 0) + NVL(SUM(walk), 0) + NVL(SUM(communication), 0)
from   booking
where  hostNo = #{hostNo}


select count(review)
from   booking
where  hostNo = #{hostNo}


select NVL(avg(clean), 0)
from   booking
where  hostNo = #{hostNo}


select NVL(avg(food), 0)
from   booking
where  hostNo = #{hostNo}


select NVL(avg(play), 0)
from   booking
where  hostNo = #{hostNo}


select NVL(avg(walk), 0)
from   booking
where  hostNo = #{hostNo}


select NVL(avg(communication), 0)
from   booking
where  hostNo = #{hostNo}


select ph.saveName saveName,
       bo.hostNo
from   photo ph, booking bo
where  ph.bookingNo = bo.bookingNo
and    bo.hostNo = #{hostNo}
and    ph.category = #{category}


select heartNo
from   heart
where  usersNo = #{usersNo}
and    hostNo = #{hostNo}

mapper.delete:

delete heart
where  hostNo = #{hostNo}
and    usersNo = #{usersNo}

mapper.update:

update booking
set    status = #{status}
where  bookingNo = #{bookingNo}


update users
set    userstype = 2
where  usersNo = #{usersNo}
