mapper.select:
      
		select tg.target,
		       u.usersNo,
		       u.id,
		       u.name,
		       u.usersType,
		       tg.me
		from (
		      (select usersTo target,
		              usersFrom me
		       from messege
		       where usersFrom = #{usersNo}
		       )  
		       union
		      (select usersFrom, 
		              usersTo mee
		       from messege
		       where usersTo = #{usersNo}
		       )  
		      ) tg, users u
		where tg.target = u.usersNo
