mapper.select:

   select  tg.target,
           tg.roomNo,
           u.id,
           u.name,
           u.usersType
   from (
           (select usersTo target,
                   roomno roomNo
           from messege
           where usersFrom = #{usersNo} 
           )  
           union
           (select usersFrom  ,
                   roomno
           from messege
           where usersTo = #{usersNo}
           )  
         ) tg,
         users u
         where tg.target = u.usersNo


   select tg.target,
          u.usersNo,
          u.id,
          u.name,
          u.usersType
   from (
         (select usersTo target,
                 roomno roomNo
          from messege
          where usersFrom = #{usersNo}
          )  
          union
         (select usersFrom,
                 roomno
          from messege
          where usersTo = #{usersNo}
          )  
         ) tg, users u, messege m
   where tg.target = u.usersNo
   and   m.roomno = tg.roomNo
